							inner join ref_QuoteStatus RQS on RQS.Id = AB.Status_Id
							where AB.Id=@id
							"""
							con.eachRow(sqlStmt)
							{
								getRow ->
								sql_statusId=getRow.Status_Id
								sql_Status=getRow.Name

								if(sql_statusId.toString().equals("3") && sql_Status.toString().equals("Pending (Quotation)"))
								{
									log.info "SQL response received as =>"+sql_AppId+","+sql_NoteId+","+sql_BrokerContactId+","+sql_Message+","+sql_Status
									arrCellVal=[sql_AppId, sql_Message, sql_BrokerContactId, sql_Status]
									RT.writeDetails(source, counter, api_startCol, rowStyle, arrCellVal)
									total_size++
									counter++
									try
									{
										RT.saveExcelFile(summary_File,O_Summary)
									}
									catch(Exception e)
									{
									  log.info e.getMessage();	
									}
								}
								else
								{
									log.info "SQL response received as =>"+sql_AppId+","+sql_NoteId+","+sql_BrokerContactId+","+sql_Message+","+sql_Status
									arrCellVal=[sql_AppId, sql_Message, sql_BrokerContactId, sql_Status]
									RT.writeDetails(source, counter, api_startCol, rowStyle, arrCellVal)
									total_size++
									counter++
									try
									{
										RT.saveExcelFile(summary_File,O_Summary)
									}
									catch(Exception e)
									{
									  log.info e.getMessage();	
									}
								}
							}			
						}				
					}//end of sql
			}//end of for loop
			con.close()

			int db_startCol=0
			counter=row_start;
			totalColumns = source.getRow(1).getLastCellNum();
			int api_endCol=totalColumns-1
			dsc='Compares API data for Handle Referral wih message created for qupte and crm identifiers with UTOM Database data'
			tcid=tcModuleName.toString()
			RT.process_Data(O_Summary, source, api_startCol, db_startCol, row_start, api_endCol, db_endCol, total_size, db_size, summary, summary_File, uri, tcid,dsc)
		}//inner if end
	}//outer if end
}//outer for end
