public String  removeCommaAndBrackets(String field)
{
	if(field!=null && field.toString().contains(","))
	{
		field=field.replace(",", " ")
	}

	if(field!=null && field.contains("["))
	{
		field=field.replace("[","")
		field=field.replace("]","")
	}
	if(field!=null)
		field=field.toUpperCase()
	return field
}

