public String removeComma(String field) { if(field!=null && field.toString().contains(",")) { field=field.replace(",", " ") } if(field!=null && field.contains("[") || field.contains("]")) { field=field.replace("[", " ") field=field.replace("]", " ") } return field } public String removeDotFromCurrency(String field) { if(field!=null && field.contains(".00")) field=field.replace(".00","") return field }
